import{_ as a,E as n,c as r,J as s,w as i,o as l,m as e,a as o}from"./chunks/framework.DA-dnmTz.js";const L=JSON.parse('{"title":"Rozhovor s Pavlem Pelikánem","description":"","frontmatter":{"title":"Rozhovor s Pavlem Pelikánem","date":"2022-01-11T00:00:00.000Z","author":"Lenka Krausová","excerpt":"Krátký rozhovor s jedním z 3SB trenérů a zároveň jedním z nejlepších hráčů v ČR.\\n","sidebar":false},"headers":[],"relativePath":"blog/2022/01/interview-pelikan.md","filePath":"blog/2022/01/interview-pelikan.md"}'),d={name:"blog/2022/01/interview-pelikan.md"},m=e("p",null,"Jak jsme již minule napověděli, můžete si přečíst rozhovor s Pavlem Pelikánem zvaným Peli, který se právě vrátil z Itálie, kde byl na ERASMU (studenti studují kratší období v jiných státech). V rozhovoru se rozepíše o italském způsobu výběru týmu i o novém projektu UIA.",-1),v=e("h2",{id:"proc-ses-pro-tym-rozhodl-delat-i-neco-vic-nez-jen-hrat",tabindex:"-1"},[o("Proč ses pro tým rozhodl dělat i něco víc než jen hrát? "),e("a",{class:"header-anchor",href:"#proc-ses-pro-tym-rozhodl-delat-i-neco-vic-nez-jen-hrat","aria-label":'Permalink to "Proč ses pro tým rozhodl dělat i něco víc než jen hrát?"'},"​")],-1),k=e("p",null,"Krátká odpověď je, že mě to prostě baví. S naším týmem jsem zažil spoustu krásných zážitků na hřišti i mimo něj, tak se to snažím kolektivu nějakým způsobem vrátit.",-1),p=e("h2",{id:"co-pro-tym-delas-jake-mas-nebo-jsi-mel-funkce",tabindex:"-1"},[o("Co pro tým děláš, jaké máš nebo jsi měl funkce? "),e("a",{class:"header-anchor",href:"#co-pro-tym-delas-jake-mas-nebo-jsi-mel-funkce","aria-label":'Permalink to "Co pro tým děláš, jaké máš nebo jsi měl funkce?"'},"​")],-1),h=e("p",null,"Dělal jsem kapitána klučičí části v letech 2018 a 2019. V školním roce 2019/2020 jsem odjel pryč z republiky na erasmus a přesunul jsem se do VR (výkonná rada), kde jsem se snažil starat o nováčky. Loni se nám podařilo vrátit na hřiště po první vlně koronaviru celkem brzy a vedl jsem tréninky pro začínající. Letos tréninky začnou až od září, možná zvládneme nějaké i v srpnu, to se ještě uvidí.",-1),u=e("h2",{id:"co-to-obnasi",tabindex:"-1"},[o("Co to obnáší? "),e("a",{class:"header-anchor",href:"#co-to-obnasi","aria-label":'Permalink to "Co to obnáší?"'},"​")],-1),c=e("p",null,"Jako kapitán jsem se staral o chod jedné části týmu. Tedy od přípravy tréninků, plánování sezóny až po vedení týmu na samotných turnajích. Nutno podotknout, že u nás v týmu není člověk na vedení všech tréninků sám. Máme další zkušené hráče, kteří jsou schopní a ochotní nějaký trénink vést nebo pomoci s dílčí částí tréninku/soustředění. Díky za ně!",-1),j=e("h2",{id:"jak-dlouho-ti-trva-pripravit-program-na-dvouhodinovy-trenink",tabindex:"-1"},[o("Jak dlouho ti trvá připravit program na dvouhodinový trénink? "),e("a",{class:"header-anchor",href:"#jak-dlouho-ti-trva-pripravit-program-na-dvouhodinovy-trenink","aria-label":'Permalink to "Jak dlouho ti trvá připravit program na dvouhodinový trénink?"'},"​")],-1),b=e("p",null,"Co tě na trénování baví? Délka přípravy je různá. Uvedu jeden příklad: záleží jestli jde o trénink, dva před turnajem nebo o trénink úplných nováčků. Možná to někoho překvapí, ale ty pro nováčky zaberou více času. Snažím se k nim přistupovat zodpovědně – vybrat cvičení, která jim budou dávat smysl a budou dobře učit nějakou dovednost pro náš sport. Dále je nutné, aby ty jednotlivé tréninky na sebe navazovaly a rozvíjely hráče v co možná nejvíce aspektech. Zároveň se učím vysvětlovat ty „věci“ tak, aby byly pro všechny pochopitelné a každý si z toho něco odnesl.",-1),y=e("h2",{id:"bavi-te-vic-hrat-nebo-trenovat-popripade-nejake-tve-dalsi-funkce-proc",tabindex:"-1"},[o("Baví tě víc hrát nebo trénovat popřípadě nějaké tvé další funkce? Proč? "),e("a",{class:"header-anchor",href:"#bavi-te-vic-hrat-nebo-trenovat-popripade-nejake-tve-dalsi-funkce-proc","aria-label":'Permalink to "Baví tě víc hrát nebo trénovat popřípadě nějaké tvé další funkce? Proč?"'},"​")],-1),z=e("p",null,[o("Hrát, tady se není moc o čem bavit. Jsem ve věku, kdy to hraní je pro mě důležitější. Zároveň jsme rozvíjející se sport, což chápu a proto se snažím zkušenosti předávat dál. Máme totiž neuvěřitelně mladý a perspektivní tým. Loňskou kvalifikaci na MČR mix jsem měl tu čest vést náš mladý C tým a byla to skvělá zkušenost. Vidět, jak se tým a jednotlivci v turnaji zlepšují byl zážitek a jsem rád, že jsem toho byl součástí. Společně s kamarády Čégem, Lipim a Terkou jsme dali dohromady projekt "),e("a",{href:"https://uia.cz/",target:"_blank",rel:"noreferrer"},"UIA"),o(" a kromě jiného letos pořádáme druhý ročník kempu PSD pro pokročilé hráče. Moc se tam těším.")],-1),_=e("h2",{id:"kdyz-delame-tento-rozhovor-tak-jsi-v-italii-co-tam-delas-predpokladam-ze-hrajes-frisbee",tabindex:"-1"},[o("Když děláme tento rozhovor, tak jsi v Itálii. Co tam děláš? Předpokládám, že hraješ frisbee. "),e("a",{class:"header-anchor",href:"#kdyz-delame-tento-rozhovor-tak-jsi-v-italii-co-tam-delas-predpokladam-ze-hrajes-frisbee","aria-label":'Permalink to "Když děláme tento rozhovor, tak jsi v Itálii. Co tam děláš? Předpokládám, že hraješ frisbee."'},"​")],-1),f=e("p",null,"Je to tak, jsem v italské Bologni. Probíhají tu tryouty do týmu úřadujících mistrů Evropy (open) z roku 2019, CUSB La Fotta.",-1),P=e("h2",{id:"jak-vybery-probihaji-je-tezke-se-do-tymu-dostat",tabindex:"-1"},[o("Jak výběry probíhají? Je těžké se do týmu dostat? "),e("a",{class:"header-anchor",href:"#jak-vybery-probihaji-je-tezke-se-do-tymu-dostat","aria-label":'Permalink to "Jak výběry probíhají? Je těžké se do týmu dostat?"'},"​")],-1),x=e("p",null,"Začalo se v pondělí 5. července, měli jsme celkem 5 vypsaných tréninků. Na základě těchto tréninků si kapitáni vybrali užší výběr (32), který pozvali na třídenní víkendové soustředění. Tréninky mají nejčastěji 2,5 až 3 hodiny a jsou velmi intenzivní. Polovina týmu, 13 hráčů, je vybraných dopředu. Zbytek týmu se vybírá z přihlášených hráčů, je nás plus mínus 35. Na prvních trénincích nás bylo vždy přes 40 hráčů, takže o soutěživost není nouze. O víkendu nás už bylo méně. Kapitáni si po skončení tryoutů sednou a vyberou si hráče, kteří se jim do týmu budou nejvíce hodit.",-1),J=e("h2",{id:"a-jak-dlouho-denne-premyslis-nad-frisbeem",tabindex:"-1"},[o("A jak dlouho denně přemýšlíš nad frisbeem? "),e("a",{class:"header-anchor",href:"#a-jak-dlouho-denne-premyslis-nad-frisbeem","aria-label":'Permalink to "A jak dlouho denně přemýšlíš nad frisbeem?"'},"​")],-1),g=e("p",null,"Dřív to bylo 24/7, teď už se v klidu vyspím 😉",-1),C=e("p",null,"A s radostí vám můžeme oznámit, že Peli se stal součástí italského týmu po úspěšném zvládnutí tryoutů.",-1);function B(S,D,N,V,w,A){const t=n("Post");return l(),r("div",null,[s(t,null,{default:i(()=>[m,v,k,p,h,u,c,j,b,y,z,_,f,P,x,J,g,C]),_:1})])}const M=a(d,[["render",B]]);export{L as __pageData,M as default};
